#CS275 - Final Project Queries

#Table Creation

#Conferences
CREATE TABLE conferences (
  conferenceId SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
  conferenceName VARCHAR(20) NOT NULL,
  PRIMARY KEY  (conferenceId),
  UNIQUE (conferenceName)
)ENGINE=InnoDB DEFAULT CHARSET=utf8;

#Positions
CREATE TABLE positions (
  positionId SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
  positionName VARCHAR(30) NOT NULL,
  positionAbbrev VARCHAR(2) NOT NULL,
  PRIMARY KEY  (positionId),
  UNIQUE (positionAbbrev)
)ENGINE=InnoDB DEFAULT CHARSET=utf8;

#Countries
CREATE TABLE countries (
  countryId SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
  countryName VARCHAR(45) NOT NULL,
  PRIMARY KEY  (countryId),
  UNIQUE (countryName)
)ENGINE=InnoDB DEFAULT CHARSET=utf8;

#Divisions
CREATE TABLE divisions (
  divisionId SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
  divisionName VARCHAR(20) NOT NULL,
  conferenceId SMALLINT UNSIGNED,
  PRIMARY KEY  (divisionId),
  FOREIGN KEY (conferenceId) REFERENCES conferences(conferenceId),
  UNIQUE (divisionName)
)ENGINE=InnoDB DEFAULT CHARSET=utf8;

#Teams
CREATE TABLE teams (
  teamId SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
  teamCity VARCHAR(35) NOT NULL,
  teamName VARCHAR(35) NOT NULL,
  divisionId SMALLINT UNSIGNED,
  PRIMARY KEY  (teamId),
  FOREIGN KEY (divisionId) REFERENCES divisions(divisionId),
  UNIQUE (teamName)
)ENGINE=InnoDB DEFAULT CHARSET=utf8;

#Coaches
CREATE TABLE coaches (
  coachId SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
  coachFirstName VARCHAR(35) NOT NULL,
  coachLastName VARCHAR(35) NOT NULL,
  teamId SMALLINT UNSIGNED,
  PRIMARY KEY  (coachId),
  FOREIGN KEY (teamId) REFERENCES teams(teamId)
)ENGINE=InnoDB DEFAULT CHARSET=utf8;

#Players
CREATE TABLE players (
  playerId SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
  playerFirstName VARCHAR(35) NOT NULL,
  playerLastName VARCHAR(35) NOT NULL,
  playerNumber SMALLINT UNSIGNED,
  playerHeight SMALLINT UNSIGNED,
  playerWeight SMALLINT UNSIGNED,
  playerDOB DATE,
  playerESPNId MEDIUMINT UNSIGNED,
  countryId SMALLINT UNSIGNED,
  teamId SMALLINT UNSIGNED,
  PRIMARY KEY  (playerId),
  FOREIGN KEY (countryId) REFERENCES countries(countryId),
  FOREIGN KEY (teamId) REFERENCES teams(teamId)
)ENGINE=InnoDB DEFAULT CHARSET=utf8;

#Player_Positions
CREATE TABLE player_positions (
  id SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
  playerId SMALLINT UNSIGNED,
  positionId SMALLINT UNSIGNED,
  PRIMARY KEY  (id,playerId,positionId),
  FOREIGN KEY (playerId) REFERENCES players(playerId),
  FOREIGN KEY (positionId) REFERENCES positions(positionId)
)ENGINE=InnoDB DEFAULT CHARSET=utf8;



